// AQI Breakpoints for PM2.5 and PM10
const AQI_BREAKPOINTS = {
    pm2_5: [
        { concentrationLow: 0, concentrationHigh: 12, AQILow: 0, AQIHigh: 50 },
        { concentrationLow: 12.1, concentrationHigh: 35.4, AQILow: 51, AQIHigh: 100 },
        { concentrationLow: 35.5, concentrationHigh: 55.4, AQILow: 101, AQIHigh: 150 },
        { concentrationLow: 55.5, concentrationHigh: 150.4, AQILow: 151, AQIHigh: 200 },
        { concentrationLow: 150.5, concentrationHigh: 250.4, AQILow: 201, AQIHigh: 300 },
    ],
    pm10: [
        { concentrationLow: 0, concentrationHigh: 54, AQILow: 0, AQIHigh: 50 },
        { concentrationLow: 55, concentrationHigh: 154, AQILow: 51, AQIHigh: 100 },
        { concentrationLow: 155, concentrationHigh: 254, AQILow: 101, AQIHigh: 150 },
        { concentrationLow: 255, concentrationHigh: 354, AQILow: 151, AQIHigh: 200 },
        { concentrationLow: 355, concentrationHigh: 424, AQILow: 201, AQIHigh: 300 },
    ]
};

// Function to calculate AQI
function calculateAQI(sensorType, value) {
    const breakpoints = AQI_BREAKPOINTS[sensorType];
    if (!breakpoints) return null; // No AQI calculation for this sensor type

    for (const breakpoint of breakpoints) {
        if (value >= breakpoint.concentrationLow && value <= breakpoint.concentrationHigh) {
            return ((breakpoint.AQIHigh - breakpoint.AQILow) / 
                    (breakpoint.concentrationHigh - breakpoint.concentrationLow)) *
                    (value - breakpoint.concentrationLow) + breakpoint.AQILow;
        }
    }
    return null; // Value is out of range
}

// Process data based on sensor type
msg.measurement = msg.payload.sensor_type; // Use sensor_type as measurement name

let value = parseFloat(msg.payload.value);
let aqi = null; // Default AQI value

// Calculate AQI only for PM2.5 and PM10
if (msg.payload.sensor_type === "pm2_5" || msg.payload.sensor_type === "pm10") {
