let alerts = [];

// Process each sensor entry
payload.forEach(sensorData => {
    const { measurement, fields, tags, timestamp } = sensorData;

    if (fields.value > thresholds[measurement]) {
        alerts.push({
            domain: "Smart Cities",
            sensor: tags.sensor_id,
            location: `${tags.city} (${fields.latitude}, ${fields.longitude})`,
            condition: `${measurement} exceeds threshold: ${fields.value} (Threshold: ${thresholds[measurement]})`,
            timestamp: timestamp
        });
    }
});

if (alerts.length > 0) {
    let alertMessage = `üö® *Alert* üö®\n\n`;

    alerts.forEach(alert => {
        alertMessage += `*Domain:* ${alert.domain}\n`;
        alertMessage += `*Sensor:* ${alert.sensor}\n`;
        alertMessage += `*Location:* ${alert.location}\n`;
        alertMessage += `*Condition:* ${alert.condition}\n`;
    });

    msg.payload = {
        chatId: parseInt(env.get("NODE_RED_TELEGRAM_MONITORING_CHAT_ID"), 10) || "-4603413735",
        type: "message",
        content: alertMessage
    };

    return msg;
} else {
    return null;
}

let thresholds = global.get("thresholds") || {
    pm2_5: 50,          // ¬µg/m¬≥
    pm10: 50,           // ¬µg/m¬≥
    co2: 1000,          // ppm
    no2: 50,            // ¬µg/m¬≥
    temperature: 35,    // ¬∞C
    humidity: 70,       // %
    power_consumption: 1.0 // kW
};

const message = msg.payload.content;
const chatId = msg.payload.chatId;

// Helper function to get units for each metric
function getUnit(metric) {
    const units = {
        pm2_5: "¬µg/m¬≥",
        pm10: "¬µg/m¬≥",
        co2: "ppm",
        no2: "¬µg/m¬≥",
        temperature: "¬∞C",
        humidity: "%",
        power_consumption: "kW"
    };
    return units[metric] || "";
}

// Prepare the available commands message
const availableCommands = `
‚ÑπÔ∏è *Available Commands:*
1Ô∏è‚É£ /get_thresholds - View current thresholds.
2Ô∏è‚É£ /set_threshold <metric> <value> - Update a threshold (e.g., /set_threshold pm2_5 45).
`;

// Handle /get_thresholds command
if (message === "/get_thresholds") {
    msg.payload = {
        chatId: chatId,
        type: "message",
        content: `üìä *Current Thresholds:*\n\n${Object.entries(thresholds)
            .map(([key, value]) => `*${key}:* ${value} ${getUnit(key)}`)
            .join("\n")}`
    };
    return msg;
}

// Handle /set_threshold command
if (message.startsWith("/set_threshold")) {
    const parts = message.split(" ");
    if (parts.length === 3) {
        const metric = parts[1];
        const value = parseFloat(parts[2]);

        if (thresholds.hasOwnProperty(metric)) {
            thresholds[metric] = value; // Update the threshold
            global.set("thresholds", thresholds); // Persist the updated thresholds
            node.warn("Updated thresholds: " + JSON.stringify(thresholds)); // Debug log

            msg.payload = {
                chatId: chatId,
                type: "message",
                content: `‚úÖ Threshold for ${metric.toUpperCase()} set to ${value} ${getUnit(metric)}`
            };
            return msg;
        } else {
            msg.payload = {
                chatId: chatId,
                type: "message",
                content: `‚ùå Invalid metric. Available metrics: ${Object.keys(thresholds).join(", ")}`
            };
            return msg;
        }
    } else {
        msg.payload = {
            chatId: chatId,
            type: "message",
            content: `‚ùå Invalid command format. Use: /set_threshold <metric> <value>\n\n${availableCommands}`
        };
        return msg;
    }
}

// Fallback message for unrelated or unrecognized commands
msg.payload = {
    chatId: chatId,
    type: "message",
    content: `‚ùì Unrecognized command or message. Please use one of the following commands:\n${availableCommands}`
};

return msg;

